import React, {Component} from "react";
import { Row, Col } from 'antd';
import axios from "axios"; 

import SatSetting from './SatSetting';
import SatelliteList from './SatelliteList';
import { SAT_API_KEY, NEARBY_SATELLITE, STARLINK_CATEGORY} from "../constants"; 
import WorldMap from './WorldMap';

/*
js中的import的格式是： import {模块中的方法} from '模块的路径'
其中模块的路径可以是相对路径，也可以是绝对路径，但是一定要加上模块的后缀名，否则会报错。
{} 里面的方法是模块中导出的方法，如果是默认导出的方法，那么就不需要加上{}，直接导入就可以了。
import React, {Component} from "react"; 从react模块中导入React和Component方法。
React是一个类，Component是一个方法。
import {Row, Col } from 'antd'; 从antd模块中导入Row和Col方法。
 */

class Main extends Component{
	constructor(){
		super();
		this.state = {
			setting: {},
			satInfo: {},
			satList: [],
			isLoadingList: false
		}
	}

	render() {
		const { isLoadingList, satInfo, satList, setting } = this.state;

		return (
			<Row className='main'>
				<Col span={8} className='left-side'>
					<SatSetting onShow={this.showNearbySatellite} />
					<SatelliteList satInfo={satInfo}
								   isLoad={isLoadingList}
								   onShowMap={this.showMap}
					/>
				</Col>
				<Col span={16} className='right-side'>
					<WorldMap satData={satList} observerData={setting} />
				</Col>
			</Row>
		);
	}







	showMap = (selected) => {
		this.setState(preState => ({
			...preState,
			satList: [...selected]

		}))
	}

	showNearbySatellite = (setting) => {
		console.log('show nearby')
		this.setState({
			isLoadingList: true,
			setting: setting
		})
		this.fetchSatellite(setting);
	}


	fetchSatellite = (setting) => {
		console.log("fetching")
		const { latitude, longitude, elevation, altitude } = setting;
		const url = `/api/${NEARBY_SATELLITE}/${latitude}/${longitude}/${elevation}/${altitude}/${STARLINK_CATEGORY}/&apiKey=${SAT_API_KEY}`;
		this.setState({
			isLoadingList: true
		});

		axios.get(url)
			.then( response => {
				console.log(response.data);
				this.setState({
					satInfo: response.data,
					isLoadingList: false
				})
			})
			.catch( error => {
				console.log('err in fetch satellite -> ', error);
			})

	}




}
export default Main;
